// Template styling helper
function getTemplateStyles(template) {
    switch (template) {
        case "professional":
            return {
                titleColor: "2F4F4F",
                subtitleColor: "696969",
                headerColor: "2F4F4F",
                contentColor: "000000"
            };
        case "modern":
            return {
                titleColor: "4A90E2",
                subtitleColor: "7ED321",
                headerColor: "4A90E2",
                contentColor: "333333"
            };
        default: // basic
            return {
                titleColor: "000000",
                subtitleColor: "666666",
                headerColor: "000000",
                contentColor: "333333"
            };
    }
}
